import passport from "passport";
import routes from "../routes";
import User from "../models/User";



export const getJoin = (req, res) => {
    res.render("join",{pageTitle:"Join"});
};

export const postJoin = async (req,res,next)=>{
    const {
        body : {name,email,password,password2}
    } = req;
    if(password !== password2){
        res.status(400);
        res.render("join",{pageTitle:"Join"});
    }else{
        try{
        const user = await User({
            name : name,
            email : email,
            avatarUrl : null
        });
        await User.register(user,password);
        next();
        }catch(error){
            console.log(error);
            res.redirect(routes.home);
        }
    }    
};

export const getUserDetail = async (req,res) => {
    const {
        params : {id}
    } =req;
    try{
    const user = await User.findById(id).populate("videos");
    console.log(user);
    if(user.avatarUrl== null){user.avatarUrl=routes.defaultImage;}
    res.render("userDetail",{pageTitle : "User Detail",user : user,loggedUser : req.user});
    }catch(error){
        console.log(error);
        res.redirect(routes.home);
    }
}

export const githubLogin = passport.authenticate("github",{failureRedirect:`${routes.login}`});


export const postGithubLogin =(req,res)=> res.redirect(routes.githubCallback); 


export const githubLoginCallback = async (_, __, profile, cb) =>{
    const { 
        _json : {id, avatar_url, name,email}
    } = profile;
    try{
        const user = await User.findOne({email});
        if(user){
            user.githubId = id;
            user.email = email;
            user.avatarUrl = avatar_url;
            user.save();
            return cb(null,user);
        }
        const newUser = await User.create({
                email : email,
                name : name,
                githubId: id,
                avatarUrl:avatar_url
            });
            return cb(null,newUser);
        
    }catch(err){
        return cb(error);
    }
};

export const me = async (req,res) =>{
    const {
        user : {id}
    } =req;
    try{
